  KEY `shikimori_id` (`shikimori_id`),
  KEY `mdl_id` (`mdl_id`),
  KEY `year` (`year`),
  KEY `type` (`type`),
  KEY `news_id` (`news_id`),
  KEY `tv_status` (`tv_status`),
  KEY `error` (`error`)
) ENGINE=InnoDB DEFAULT CHARSET={charset} COMMENT='База модуля';

